**基本的なMCPクライアント実装：**

```typescript
// MCP Client実装例
import { Client } from '@modelcontextprotocol/sdk/client/index.js';
import { StdioClientTransport } from '@modelcontextprotocol/sdk/client/stdio.js';

class MCPClientManager {
    private clients: Map<string, Client> = new Map();

    async connectToServer(serverName: string, command: string, args: string[]) {
        try {
            // Stdio transport作成
            const transport = new StdioClientTransport({
                command: command,
                args: args
            });

            // クライアント作成
            const client = new Client({
                name: `my-client-${serverName}`,
                version: "1.0.0"
            }, {
                capabilities: {
                    tools: {},
                    resources: {},
                    prompts: {}
                }
            });

            // 接続
            await client.connect(transport);

            // 利用可能なツールを取得
            const tools = await client.listTools();
            console.log(`Available tools from ${serverName}:`, tools);

            this.clients.set(serverName, client);

            return client;
        } catch (error) {
            console.error(`Failed to connect to ${serverName}:`, error);
            throw error;
        }
    }

    async callTool(serverName: string, toolName: string, arguments_: any) {
        const client = this.clients.get(serverName);
        if (!client) {
            throw new Error(`Client ${serverName} not connected`);
        }

        try {
            const result = await client.callTool({
                name: toolName,
                arguments: arguments_
            });

            return result;
        } catch (error) {
            console.error(`Tool call failed:`, error);
            throw error;
        }
    }

    async getResource(serverName: string, uri: string) {
        const client = this.clients.get(serverName);
        if (!client) {
            throw new Error(`Client ${serverName} not connected`);
        }

        try {
            const resource = await client.readResource({ uri });
            return resource;
        } catch (error) {
            console.error(`Resource read failed:`, error);
            throw error;
        }
    }

    async disconnect(serverName: string) {
        const client = this.clients.get(serverName);
        if (client) {
            await client.close();
            this.clients.delete(serverName);
        }
    }

    async disconnectAll() {
        for (const [name, client] of this.clients) {
            await client.close();
        }
        this.clients.clear();
    }
}
```

