```typescript
class RobustMCPClient {
    private client: Client | null = null;
    private reconnectAttempts = 0;
    private maxReconnectAttempts = 3;

    async connectWithRetry(serverConfig: ServerConfig) {
        while (this.reconnectAttempts < this.maxReconnectAttempts) {
            try {
                await this.connect(serverConfig);
                this.reconnectAttempts = 0;
                return;
            } catch (error) {
                this.reconnectAttempts++;
                console.log(`Connection attempt ${this.reconnectAttempts} failed:`, error);

                if (this.reconnectAttempts < this.maxReconnectAttempts) {
                    await new Promise(resolve => setTimeout(resolve, 1000 * this.reconnectAttempts));
                }
            }
        }
        throw new Error('Max reconnection attempts reached');
    }

    async safeToolCall(toolName: string, args: any) {
        if (!this.client) {
            throw new Error('Client not connected');
        }

        try {
            return await this.client.callTool({
                name: toolName,
                arguments: args
            });
        } catch (error) {
            console.error(`Tool call failed, attempting reconnection:`, error);

            // 再接続を試行
            await this.connectWithRetry(this.lastServerConfig);

            // リトライ
            return await this.client!.callTool({
                name: toolName,
                arguments: args
            });
        }
    }
}
