**基本的なMCPサーバー実装：**

```typescript
// mcp-server.ts
import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import {
    CallToolRequestSchema,
    ListToolsRequestSchema,
    CallToolResult,
    ListToolsResult
} from '@modelcontextprotocol/sdk/types.js';
import fs from 'fs/promises';
import path from 'path';

class FileSystemMCPServer {
    private server: Server;

    constructor() {
        this.server = new Server(
            {
                name: "filesystem-server",
                version: "1.0.0"
            },
            {
                capabilities: {
                    tools: {},
                    resources: {}
                }
            }
        );

        this.setupToolHandlers();
        this.setupResourceHandlers();
    }

    private setupToolHandlers() {
        // ツール一覧の提供
        this.server.setRequestHandler(ListToolsRequestSchema, async () => {
            return {
                tools: [
                    {
                        name: "read_file",
                        description: "ファイルの内容を読み取ります",
                        inputSchema: {
                            type: "object",
                            properties: {
                                path: {
                                    type: "string",
                                    description: "読み取るファイルのパス"
                                }
                            },
                            required: ["path"]
                        }
                    },
                    {
                        name: "write_file",
                        description: "ファイルに内容を書き込みます",
                        inputSchema: {
                            type: "object",
                            properties: {
                                path: {
                                    type: "string",
                                    description: "書き込むファイルのパス"
                                },
                                content: {
                                    type: "string",
                                    description: "書き込む内容"
                                }
                            },
                            required: ["path", "content"]
                        }
                    },
                    {
                        name: "list_directory",
                        description: "ディレクトリの内容を一覧表示",
                        inputSchema: {
                            type: "object",
                            properties: {
                                path: {
                                    type: "string",
                                    description: "一覧するディレクトリのパス"
                                }
                            },
                            required: ["path"]
                        }
                    },
                    {
                        name: "search_files",
                        description: "ファイル内容を検索",
                        inputSchema: {
                            type: "object",
                            properties: {
                                directory: {
                                    type: "string",
                                    description: "検索するディレクトリ"
                                },
                                pattern: {
                                    type: "string",
                                    description: "検索パターン（正規表現）"
                                },
                                file_extension: {
                                    type: "string",
                                    description: "対象ファイル拡張子"
                                }
                            },
                            required: ["directory", "pattern"]
                        }
                    }
                ]
            } as ListToolsResult;
        });

        // ツール実行の処理
        this.server.setRequestHandler(CallToolRequestSchema, async (request) => {
            const { name, arguments: args } = request.params;

            try {
                switch (name) {
                    case "read_file":
                        return await this.readFile(args.path);

                    case "write_file":
                        return await this.writeFile(args.path, args.content);

                    case "list_directory":
                        return await this.listDirectory(args.path);

                    case "search_files":
                        return await this.searchFiles(
                            args.directory,
                            args.pattern,
                            args.file_extension
                        );

                    default:
                        throw new Error(`Unknown tool: ${name}`);
                }
            } catch (error) {
                return {
                    content: [
                        {
                            type: "text",
                            text: `Error: ${error.message}`
                        }
                    ],
                    isError: true
                };
            }
        });
    }

    private async readFile(filePath: string): Promise<CallToolResult> {
        try {
            // セキュリティ: パストラバーサル攻撃の防止
            const safePath = path.resolve(filePath);
            const allowedDir = path.resolve('./allowed-directory');

            if (!safePath.startsWith(allowedDir)) {
                throw new Error('Access denied: Path outside allowed directory');
            }

            const content = await fs.readFile(safePath, 'utf-8');

            return {
                content: [
                    {
                        type: "text",
                        text: content
                    }
                ]
            };
        } catch (error) {
            throw new Error(`Failed to read file: ${error.message}`);
        }
    }

    private async writeFile(filePath: string, content: string): Promise<CallToolResult> {
        try {
            const safePath = path.resolve(filePath);
            const allowedDir = path.resolve('./allowed-directory');

            if (!safePath.startsWith(allowedDir)) {
                throw new Error('Access denied: Path outside allowed directory');
            }

            await fs.writeFile(safePath, content, 'utf-8');

            return {
                content: [
                    {
                        type: "text",
                        text: `Successfully wrote to ${filePath}`
                    }
                ]
            };
        } catch (error) {
            throw new Error(`Failed to write file: ${error.message}`);
        }
    }

    private async listDirectory(dirPath: string): Promise<CallToolResult> {
        try {
            const safePath = path.resolve(dirPath);
            const entries = await fs.readdir(safePath, { withFileTypes: true });

            const result = entries.map(entry => ({
                name: entry.name,
                type: entry.isDirectory() ? 'directory' : 'file',
                path: path.join(dirPath, entry.name)
            }));

            return {
                content: [
                    {
                        type: "text",
                        text: JSON.stringify(result, null, 2)
                    }
                ]
            };
        } catch (error) {
            throw new Error(`Failed to list directory: ${error.message}`);
        }
    }

    private async searchFiles(
        directory: string,
        pattern: string,
        fileExtension?: string
    ): Promise<CallToolResult> {
        try {
            const regex = new RegExp(pattern, 'gi');
            const results: any[] = [];

            async function searchInDirectory(dir: string) {
                const entries = await fs.readdir(dir, { withFileTypes: true });

                for (const entry of entries) {
                    const fullPath = path.join(dir, entry.name);

                    if (entry.isDirectory()) {
                        await searchInDirectory(fullPath);
                    } else if (entry.isFile()) {
                        // 拡張子フィルター
                        if (fileExtension && !entry.name.endsWith(fileExtension)) {
                            continue;
                        }

                        try {
                            const content = await fs.readFile(fullPath, 'utf-8');
                            const matches = content.match(regex);

                            if (matches) {
                                results.push({
                                    file: fullPath,
                                    matches: matches.length,
                                    preview: content.substring(0, 200) + '...'
                                });
                            }
                        } catch (readError) {
                            // バイナリファイルなどはスキップ
                            continue;
                        }
                    }
                }
            }

            await searchInDirectory(directory);

            return {
                content: [
                    {
                        type: "text",
                        text: JSON.stringify({
                            pattern,
                            totalResults: results.length,
                            files: results
                        }, null, 2)
                    }
                ]
            };
        } catch (error) {
            throw new Error(`Search failed: ${error.message}`);
        }
    }

    private setupResourceHandlers() {
        // リソース一覧の提供
        this.server.setRequestHandler(
            { method: "resources/list" },
            async () => {
                return {
                    resources: [
                        {
                            uri: "file://config.json",
                            name: "Configuration",
                            description: "Server configuration file"
                        }
                    ]
                };
            }
        );

        // リソース読み取りの処理
        this.server.setRequestHandler(
            { method: "resources/read" },
            async (request) => {
                const { uri } = request.params;

                if (uri === "file://config.json") {
                    return {
                        contents: [
                            {
                                uri,
                                mimeType: "application/json",
                                text: JSON.stringify({
                                    serverName: "filesystem-server",
                                    version: "1.0.0",
                                    allowedDirectory: "./allowed-directory"
                                }, null, 2)
                            }
                        ]
                    };
                }

                throw new Error(`Resource not found: ${uri}`);
            }
        );
    }

    async start() {
        const transport = new StdioServerTransport();
        await this.server.connect(transport);
        console.error("Filesystem MCP Server started");
    }
}

// サーバー起動
if (import.meta.url === `file://${process.argv[1]}`) {
    const server = new FileSystemMCPServer();
    server.start().catch(console.error);
}

export { FileSystemMCPServer };
```

