// database-mcp-server.ts
import sqlite3 from 'sqlite3';
import { Database } from 'sqlite';

class DatabaseMCPServer {
    private db: Database | null = null;

    constructor(private dbPath: string) {
        this.setupDatabaseTools();
    }

    private async connectDatabase() {
        if (!this.db) {
            this.db = await open({
                filename: this.dbPath,
                driver: sqlite3.Database
            });
        }
        return this.db;
    }

    private setupDatabaseTools() {
        // データベース操作ツールの実装
        const tools = [
            {
                name: "execute_query",
                description: "SQLクエリを実行",
                inputSchema: {
                    type: "object",
                    properties: {
                        query: { type: "string" },
                        params: { type: "array" }
                    },
                    required: ["query"]
                }
            },
            {
                name: "get_table_schema",
                description: "テーブルスキーマを取得",
                inputSchema: {
                    type: "object",
                    properties: {
                        tableName: { type: "string" }
                    },
                    required: ["tableName"]
                }
            }
        ];

        // ツール実装...
    }

    private async executeQuery(query: string, params: any[] = []): Promise<CallToolResult> {
        try {
            const db = await this.connectDatabase();

            // セキュリティ: 危険なSQLの検出
            const dangerousKeywords = ['DROP', 'DELETE', 'TRUNCATE', 'ALTER'];
            const upperQuery = query.toUpperCase();

            const hasDangerousKeyword = dangerousKeywords.some(keyword => 
                upperQuery.includes(keyword)
            );

            if (hasDangerousKeyword) {
                throw new Error('Dangerous SQL operations are not allowed');
            }

            const result = await db.all(query, params);

            return {
                content: [
                    {
                        type: "text",
                        text: JSON.stringify(result, null, 2)
                    }
                ]
            };
        } catch (error) {
            throw new Error(`Query execution failed: ${error.message}`);
        }
    }
}
